// src/utils/logger.ts
import winston from 'winston';
import 'winston-daily-rotate-file';

interface LogContext {
  userId?: string;
  caseId?: string;
  requestId?: string;
  action?: string;
  duration?: number;
}

class Logger {
  private logger: winston.Logger;

  constructor() {
    this.logger = winston.createLogger({
      level: process.env.LOG_LEVEL || 'info',
      format: winston.format.combine(
        winston.format.timestamp(),
        winston.format.errors({ stack: true }),
        winston.format.json(),
        winston.format.printf(({ timestamp, level, message, ...meta }) => {
          return JSON.stringify({
            '@timestamp': timestamp,
            level,
            message,
            service: 'icsr-ai-backend',
            version: process.env.APP_VERSION || '1.0.0',
            environment: process.env.NODE_ENV || 'development',
            ...meta
          });
        })
      ),
      transports: [
        // Console output
        new winston.transports.Console({
          format: winston.format.combine(
            winston.format.colorize(),
            winston.format.simple()
          )
        }),

        // File output
        new winston.transports.DailyRotateFile({
          filename: 'logs/application-%DATE%.log',
          datePattern: 'YYYY-MM-DD',
          zippedArchive: true,
          maxSize: '100m',
          maxFiles: '30d'
        }),

        // Error file output
        new winston.transports.DailyRotateFile({
          filename: 'logs/error-%DATE%.log',
          datePattern: 'YYYY-MM-DD',
          level: 'error',
          zippedArchive: true,
          maxSize: '100m',
          maxFiles: '30d'
        })
      ]
    });

    // Elasticsearch transport in production
    if (process.env.NODE_ENV === 'production') {
      const { ElasticsearchTransport } = require('winston-elasticsearch');

      this.logger.add(new ElasticsearchTransport({
        level: 'info',
        clientOpts: {
          node: process.env.ELASTICSEARCH_URL || 'http://elasticsearch:9200'
        },
        index: 'icsr-ai-logs',
        indexTemplate: {
          name: 'icsr-ai-template',
          patterns: ['icsr-ai-logs-*'],
          settings: {
            number_of_shards: 1,
            number_of_replicas: 1
          },
          mappings: {
            properties: {
              '@timestamp': { type: 'date' },
              level: { type: 'keyword' },
              message: { type: 'text' },
              service: { type: 'keyword' },
              userId: { type: 'keyword' },
              caseId: { type: 'keyword' },
              requestId: { type: 'keyword' },
              action: { type: 'keyword' },
              duration: { type: 'long' },
              stack: { type: 'text' }
            }
          }
        }
      }));
    }
  }

  info(message: string, context?: LogContext): void {
    this.logger.info(message, context);
  }

  error(message: string, error?: Error | any, context?: LogContext): void {
    const errorInfo = error instanceof Error ? {
      stack: error.stack,
      name: error.name
    } : error;

    this.logger.error(message, {
      error: errorInfo,
      ...context
    });
  }

  warn(message: string, context?: LogContext): void {
    this.logger.warn(message, context);
  }

  debug(message: string, context?: LogContext): void {
    this.logger.debug(message, context);
  }

  // ì„±ëŠ¥ ë¡œê¹…
  logPerformance(action: string, duration: number, context?: LogContext): void {
    this.info(`Performance: ${action} completed`, {
      action,
      duration,
      ...context
    });
  }

  // ë¹„ì¦ˆë‹ˆìŠ¤ ì´ë²¤íŠ¸ ë¡œê¹…
  logBusinessEvent(event: string, context?: LogContext): void {
    this.info(`Business Event: ${event}`, {
      event_type: 'business',
      event_name: event,
      ...context
    });
  }

  // AI ì²˜ë¦¬ ë¡œê¹…
  logAIProcessing(modelName: string, accuracy: number, processingTime: number, context?: LogContext): void {
