    def _apply_dynamic_quantization(self, model: nn.Module) -> nn.Module:
        """ë™ì  ì–‘ìí™” ì ìš©"""
        try:
            quantized_model = quantize_dynamic(
                model,
                {nn.Linear, nn.LSTM, nn.GRU},  # ì–‘ìí™”í•  ë ˆì´ì–´ íƒ€ì…
                dtype=torch.qint8
            )

