import { sql, relations } from "drizzle-orm";
import { 
  pgTable, 
  text, 
  varchar, 
  timestamp, 
  boolean, 
  decimal, 
  integer,
  jsonb,
  pgEnum
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Enums
export const userRoleEnum = pgEnum("user_role", ["USER", "REVIEWER", "ADMIN"]);
export const caseStatusEnum = pgEnum("case_status", ["ê¸´ê¸‰", "ê²€í†  í•„ìš”", "ì²˜ë¦¬ì¤‘", "ì™„ë£Œ"]);
export const severityEnum = pgEnum("severity", ["Low", "Medium", "High", "Critical"]);
export const logSeverityEnum = pgEnum("log_severity", ["INFO", "WARNING", "HIGH"]);

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  name: text("name").notNull(),
  password: text("password").notNull(),
  role: userRoleEnum("role").notNull().default("USER"),
  organization: text("organization"),
  isActive: boolean("is_active").notNull().default(true),
  lastLoginAt: timestamp("last_login_at"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Cases table for adverse drug reactions
export const cases = pgTable("cases", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  caseNumber: text("case_number").notNull().unique(),
  patientAge: integer("patient_age").notNull(),
  patientGender: text("patient_gender").notNull(),
  drugName: text("drug_name").notNull(),
  drugDosage: text("drug_dosage"),
  adverseReaction: text("adverse_reaction").notNull(),
  reactionDescription: text("reaction_description"),
  severity: severityEnum("severity").notNull(),
  status: caseStatusEnum("status").notNull().default("ê²€í†  í•„ìš”"),
  reporterId: varchar("reporter_id").notNull().references(() => users.id),
  dateReported: timestamp("date_reported").notNull().default(sql`now()`),
  dateOfReaction: timestamp("date_of_reaction"),
  concomitantMeds: jsonb("concomitant_meds"),
  medicalHistory: text("medical_history"),
  outcome: text("outcome"),
  // Soft delete fields for pharmacovigilance compliance
  isDeleted: boolean("is_deleted").notNull().default(false),
  deletedAt: timestamp("deleted_at"),
  deletedBy: varchar("deleted_by").references(() => users.id),
  deletionReason: text("deletion_reason"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// AI predictions table
export const aiPredictions = pgTable("ai_predictions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  caseId: varchar("case_id").notNull().references(() => cases.id),
  modelName: text("model_name").notNull(),
  modelVersion: text("model_version").notNull(),
  confidence: decimal("confidence", { precision: 5, scale: 4 }).notNull(),
  prediction: jsonb("prediction").notNull(),
  recommendation: text("recommendation"),
  processingTime: decimal("processing_time", { precision: 8, scale: 3 }),
  humanReviewed: boolean("human_reviewed").notNull().default(false),
  reviewerId: varchar("reviewer_id").references(() => users.id),
  reviewNotes: text("review_notes"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Audit logs table
export const auditLogs = pgTable("audit_logs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id),
  action: text("action").notNull(),
  resource: text("resource").notNull(),
  resourceId: text("resource_id"),
  details: jsonb("details"),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  severity: logSeverityEnum("severity").notNull().default("INFO"),
  timestamp: timestamp("timestamp").notNull().default(sql`now()`),
});

// AI models table
export const aiModels = pgTable("ai_models", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  version: text("version").notNull(),
  status: text("status").notNull().default("Active"),
  accuracy: decimal("accuracy", { precision: 5, scale: 2 }),
  avgResponseTime: integer("avg_response_time"),
  totalPredictions: integer("total_predictions").notNull().default(0),
  lastUpdated: timestamp("last_updated").notNull().default(sql`now()`),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  reportedCases: many(cases),
  auditLogs: many(auditLogs),
  aiPredictionReviews: many(aiPredictions),
}));

export const casesRelations = relations(cases, ({ one, many }) => ({
  reporter: one(users, {
    fields: [cases.reporterId],
    references: [users.id],
  }),
  deletedByUser: one(users, {
    fields: [cases.deletedBy],
    references: [users.id],
  }),
  aiPredictions: many(aiPredictions),
}));

export const aiPredictionsRelations = relations(aiPredictions, ({ one }) => ({
  case: one(cases, {
    fields: [aiPredictions.caseId],
    references: [cases.id],
  }),
  reviewer: one(users, {
    fields: [aiPredictions.reviewerId],
    references: [users.id],
  }),
}));

export const auditLogsRelations = relations(auditLogs, ({ one }) => ({
  user: one(users, {
    fields: [auditLogs.userId],
    references: [users.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  lastLoginAt: true,
});

export const updateUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
}).partial();

// Custom case schema that properly handles date strings
export const insertCaseSchema = z.object({
  patientAge: z.number(),
  patientGender: z.string(),
  drugName: z.string(),
  drugDosage: z.string().nullable().optional(),
  adverseReaction: z.string(),
  reactionDescription: z.string().nullable().optional(),
  severity: z.enum(["Low", "Medium", "High", "Critical"]),
  status: z.enum(["ê¸´ê¸‰", "ê²€í†  í•„ìš”", "ì²˜ë¦¬ì¤‘", "ì™„ë£Œ"]).optional(),
  reporterId: z.string(),
  dateReported: z.coerce.date().optional(),
  dateOfReaction: z.coerce.date().nullable().optional(),
  concomitantMeds: z.any().nullable().optional(),
  medicalHistory: z.string().nullable().optional(),
  outcome: z.string().nullable().optional(),
});

// Update schema with field-level restrictions for regulatory compliance
export const updateCaseSchemaReviewer = z.object({
  // REVIEWER can only modify review-related fields
  severity: z.enum(["Low", "Medium", "High", "Critical"]).optional(),
  status: z.enum(["ê¸´ê¸‰", "ê²€í†  í•„ìš”", "ì²˜ë¦¬ì¤‘", "ì™„ë£Œ"]).optional(),
  reactionDescription: z.string().nullable().optional(),
  outcome: z.string().nullable().optional(),
});

export const updateCaseSchemaAdmin = z.object({
  // ADMIN can modify additional fields but never reporterId (immutable for audit trail)
  patientAge: z.number().optional(),
  patientGender: z.string().optional(),
  drugName: z.string().optional(),
  drugDosage: z.string().nullable().optional(),
  adverseReaction: z.string().optional(),
  reactionDescription: z.string().nullable().optional(),
  severity: z.enum(["Low", "Medium", "High", "Critical"]).optional(),
  status: z.enum(["ê¸´ê¸‰", "ê²€í†  í•„ìš”", "ì²˜ë¦¬ì¤‘", "ì™„ë£Œ"]).optional(),
  dateOfReaction: z.coerce.date().nullable().optional(),
  concomitantMeds: z.any().nullable().optional(),
  medicalHistory: z.string().nullable().optional(),
  outcome: z.string().nullable().optional(),
});

// Soft delete schema for pharmacovigilance compliance
export const softDeleteCaseSchema = z.object({
  deletionReason: z.string().min(10, "Deletion reason must be at least 10 characters"),
});

export const insertAiPredictionSchema = createInsertSchema(aiPredictions).omit({
  id: true,
  createdAt: true,
});

export const insertAuditLogSchema = createInsertSchema(auditLogs).omit({
  id: true,
  timestamp: true,
});

export const insertAiModelSchema = createInsertSchema(aiModels).omit({
  id: true,
  createdAt: true,
  lastUpdated: true,
});

// Types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertCase = z.infer<typeof insertCaseSchema>;
export type UpdateCaseReviewer = z.infer<typeof updateCaseSchemaReviewer>;
export type UpdateCaseAdmin = z.infer<typeof updateCaseSchemaAdmin>;
export type SoftDeleteCase = z.infer<typeof softDeleteCaseSchema>;
export type Case = typeof cases.$inferSelect;
export type InsertAiPrediction = z.infer<typeof insertAiPredictionSchema>;
export type AiPrediction = typeof aiPredictions.$inferSelect;
export type InsertAuditLog = z.infer<typeof insertAuditLogSchema>;
export type AuditLog = typeof auditLogs.$inferSelect;
export type InsertAiModel = z.infer<typeof insertAiModelSchema>;
export type AiModel = typeof aiModels.$inferSelect;

